const express=require("express");const app=express();const bodyParser=require("body-parser");app.use(bodyParser.urlencoded({extended:true}));app.get("/",function(b,a){a.sendFile(__dirname+"/bmiCalculator.html")});app.post("/",function(d,b){var c=Number(d.body.n1);var e=Number(d.body.n2);var a=c/(Math.pow(e,2));b.send("Your BMI count is "+a)});app.listen(4000,function(){console.log("Listtening to Port 4000")});